import { Outlet, useParams } from 'react-router-dom';
import { useState } from 'react';

import MainNavbar from '../MainNavbar.js';
import ProductNavbar from './ProductNavbar.js';

function Products() {

    const params = useParams();
    const productid = params.productid;

    /*To open accordion based on category of item */
    const [category, setCategory] = useState("supplements");
    if(
        (productid === "brain-boosters-memory-enhancers") ||
        (productid === "heart-health") ||
        (productid === "joint-health") ||
        (productid === "liver-tonic") ||
        (productid === "natural-antioxidants") ||
        (productid === "hormones-oral-contraceptives") ||
        (productid === "slimming-weight-management")
    ) {
        setCategory("supplements");
    } else if(
        (productid === "blunt-trauma") ||
        (productid === "muscle-relaxants") ||
        (productid === "wound-healing")
    ) {
        setCategory("injury");
    } else if (
        (productid === "cough-cold") ||
        (productid === "ear-nose-throat") ||
        (productid === "urinary-incontinence")
    ) {
        setCategory("illness");
    } else if (productid === "dermatological") {
        setCategory("dermatological");
    } else if (productid === "disinfectants") {
        setCategory("disinfectants");
    } else if (productid === "medical-tools") {
        setCategory("medical-tools");
    } else {
        setCategory("supplements");
    }



    return(
        <>
            <MainNavbar activeTab="/products"/>

            <div className="container section product">
                <div className="row">
                    <div className="col-12 col-sm-4">
                        <ProductNavbar activeTab={productid} category={category}/>
                    </div>


                    <div className="col-12 col-sm-8">
                        <Outlet />
                    </div>
                </div>
            </div>

            
        </>
    );
}

export default Products;
