import logo from '../images/logo.png';
import {useState, useEffect} from 'react';
import { Link, useLocation } from 'react-router-dom';


function MainNavbar() {

  const [activeTab, setActiveTab] = useState("");

  const clickHandler = (message) => {
    setActiveTab(message);
	}

  const location = useLocation(); // once ready it returns the 'window.location' object
  const [url, setUrl] = useState(null);
  useEffect(() => {
    setUrl(location.pathname);
    console.log(url);
  }, [location]);

  return (
    <>
      <nav className="navbar navbar-expand-lg bg-body-light">
        <div className="container">

        <Link className={`navbar-brand ${(url === "/") ? 'active' : ''}`} to="/"><img className="logo" src={logo} alt="logo"/></Link>


          {/* <Link className={`navbar-brand ${(activeTab === "Home") ? 'active' : ''}`} to={`${(activeTab === "Home") ? '#/' : '/'}`} onClick={() => clickHandler("Home")}> <img className="logo" src={logo} alt="logo"/></Link> */}
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className={`navbar-link ${({ isActive }) => isActive ? " active" : ""}`} aria-current="page" to="/">Home</Link>
                {/* <Link className={`nav-link ${(activeTab === "Home") ? 'active' : ''}`} aria-current="page" to={`${(activeTab === "Home") ? '#/' : '/'}`} onClick={() => clickHandler("Home")}> Home </Link> */}
              </li>
              <li className="nav-item">
                <Link className={`navbar-link ${({ isActive }) => isActive ? " active" : ""}`} to="/about">About</Link>
                {/* <Link className={`nav-link ${(activeTab === "About") ? 'active' : ''}`} to={`${(activeTab === "About") ? '#/' : '/about'}`} onClick={() => clickHandler("About")}> About us </Link> */}
              </li>
              <li className="nav-item">
                <Link className={`navbar-link ${({ isActive }) => isActive ? " active" : ""}`} to="/products">Products</Link>
                {/* <Link className={`nav-link ${(activeTab === "Products") ? 'active' : ''}`} to={`${(activeTab === "Products") ? '#/' : '/products'}`} onClick={() => clickHandler("Products")}> Our products </Link> */}
              </li>
              <li className="nav-item">
                <a className={`nav-link ${(activeTab === "Events") ? 'active' : ''}`} href="#events" onClick={() => clickHandler("Events")}>Events</a>
              </li>
              <li className="nav-item">
                <a className={`nav-link ${(activeTab === "Contact") ? 'active' : ''}`} href="#contactus" onClick={() => clickHandler("Contact")}>Contact us</a>
              </li>
            </ul>
            <form className="d-flex" role="search">
              <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
              <button className="btn btn-outline-success" type="submit">Search</button>
            </form>
          </div>
        </div>
      </nav>
      </>
  );
}

export default MainNavbar;
