import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';

/*Set images URL*/
import omega3 from '../../../images/products/supplements-natural-antioxidants/omega3.jpg';
import spirulinaHawaiian from '../../../images/products/supplements-natural-antioxidants/spirulina-hawaiian.jpg';
import timec500 from '../../../images/products/supplements-natural-antioxidants/time-c500-with-rose-hip.jpg';

function NaturalAntiOxidants () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);
    const [ingredients, setIngredients] = useState("");
    const [dosageInstructions, setDosage] = useState("");

    const category = "Supplements / Natural anti-oxidants";
    const header = "Natural anti-oxidants";
    const products = [
        {
            id:1, 
            name:"Omega-3", 
            image: omega3,
            description: "Molecular distillation processing technique produces high purity fish oil and minimizes the presence of impurities. Omega 3 helps maintain healthy heart function, improves brain function",
            packSize: "60's softgels",
            productOf: "-",
            exclusiveAvailability: false, /*Only available in clinics and specialised centers*/
            ingredients: "-", /*Separate ingredients by comma (i.e ABC, EFG, HIJ)*/
            dosageInstructions: "-",
        },
        {
            id:2, 
            name:"Spirulina Hawaiian", 
            image: spirulinaHawaiian,
            description: "Molecular distillation processing technique produces high purity fish oil and minimizes the presence of impurities. Omega 3 helps maintain healthy heart function, improves brain function",
            packSize: "90's softgels",
            productOf: "-",
            exclusiveAvailability: false, /*Only available in clinics and specialised centers*/
            ingredients: "-", /*Separate ingredients by comma (i.e ABC, EFG, HIJ)*/
            dosageInstructions: "-",
        }, 
        {
            id:3, 
            name:"Time C-500 with Rose Hip", 
            image: timec500,
            description: "High in natural antioxidants. Enhances immunity. Richest whole food source of beta-carotene. Contains the benefits of dark green vegetables. No preservatives. Free from herbicides or pesticides. Grown with ultra-pure seawater mineral source. Organically grown.",
            packSize: "30's tablets",
            productOf: "Hawaii",
            exclusiveAvailability: false, /*Only available in clinics and specialised centers*/
            ingredients: "-", /*Separate ingredients by comma (i.e ABC, EFG, HIJ)*/
            dosageInstructions: "-",
        }
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setIngredients(message.ingredients);
        setDosage(message.dosageInstructions);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability} ingredients={ingredients} dosageInstructions={dosageInstructions}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default NaturalAntiOxidants;
