import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';

/*Set images URL*/
import prospan from '../../../images/products/illness-cough-cold-preparations/prospan.png';
import prospanforte from '../../../images/products/illness-cough-cold-preparations/prospan-forte.png';

function CoughCold () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);
    const [ingredients, setIngredients] = useState("");
    const [dosageInstructions, setDosage] = useState("");

    const header = "Cough & cold preparations";
    const category = "Illness / " + header;
    const products = [
        {
            id:1, 
            name:"Prospan (Tablets)", 
            image: prospan,
            description: "Four modes of action effective against cough: dissolves thick mucus, relaxes airway muscles, alleviates inflammation and relieves cough. Alcohol-free, sugar-free, colorant-free, pleasant taste. Non-drowsy formula. Suitable for infants – adults. Available as syrup (measuring cup provided for convenience) and effervescent tablets (easy to carry around).",
            packSize: "20’s (effervescent tablets)",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Contains the unique special ivy extract EA 575", 
            dosageInstructions: "Prospan Syrup (all doses to be taken three times daily): Adults: 1 tablet in morning, 1/2 tablet in evening / Children 6 – 12 years: 1/2 tablet twice daily / Children 5 years & below: 2.5ml",
        },
        {
            id:2, 
            name:"Prospan Forte for Adults", 
            image: prospanforte,
            description: "Four modes of action effective against cough: dissolves thick mucus, relaxes airway muscles, alleviates inflammation and relieves cough. The cooling menthol helps to soothe throat irritation and calms cough quickly. Alcohol-free, sugar-free, colorant-free. Non-drowsy formula. Suitable for adults and children 6 years old and above. Available as syrup (measuring cup provided for convenience) and effervescent tablets (easy to carry around).",
            packSize: "100ml (syrup)",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Contains the unique special ivy extract EA 575 and menthol.", 
            dosageInstructions: "Prospan Syrup: Adults: 5 – 7.5ml / Children 6 – 12 years: 5ml / Children 5 years & below: 2.5ml",
        },
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setIngredients(message.ingredients);
        setDosage(message.dosageInstructions);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability} ingredients={ingredients} dosageInstructions={dosageInstructions}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default CoughCold;
