import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';

/*Set images URL*/
import cerumol from '../../../images/products/illness-ear-nose-throat-preparations/cerumol.png';
import cerumololiveoil from '../../../images/products/illness-ear-nose-throat-preparations/cerumol-olive-oil.png';
import dymista from '../../../images/products/illness-ear-nose-throat-preparations/dymista-nasal-spray.jpg';
import islajunior from '../../../images/products/illness-ear-nose-throat-preparations/isla-junior.png';
import islacassis from '../../../images/products/illness-ear-nose-throat-preparations/isla-cassis.jpg';
import nisita from '../../../images/products/illness-ear-nose-throat-preparations/nisita.jpg';

function EarNoseThroat () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);
    const [ingredients, setIngredients] = useState("");
    const [dosageInstructions, setDosage] = useState("");

    const header = "Ear, nose & throat preparations";
    const category = "Illness / " + header;
    const products = [
        {
            id:1, 
            name:"Cerumol", 
            image: cerumol,
            description: "Ear drops for removal of ear wax",
            packSize: "10ml",
            productOf: "United Kingdom",
            exclusiveAvailability: false, 
            ingredients: "Propylene Glycol Dicaprylocaprate, Butoxyl, Turpentine Oil, Chlorocresol", 
            dosageInstructions: "Put 5 drops into the ear twice daily for 3 days",
        },
        {
            id:2, 
            name:"Cerumol Olive Oil", 
            image: cerumololiveoil,
            description: "Extra virgin olive oil ear drops to gently soften and remove of ear wax.",
            packSize: "10ml",
            productOf: "Spain",
            exclusiveAvailability: false, 
            ingredients: "Medicinal grade extra virgin olive oil.", 
            dosageInstructions: "Put 2-3 drops into the ear then gently massage around the outside of the ear. Allow a few minutes for the drops to reach the eardrum by keeping the head tilted and repeat in the other ear if required. Use the drops twice daily for up to 7 days.",
        },
        {
            id:3, 
            name:"Dymista Nasal Spray", 
            image: dymista,
            description: "Symptomatic treatment of moderate to severe allergic rhinitis and rhino-conjunctivitis in adults and children 12 years and older where use of a combination (intranasal antihistamine and glucocorticoid) is appropriate.",
            packSize: "17 mL bottle containing 120 sprays",
            productOf: "-",
            exclusiveAvailability: true, 
            ingredients: "One spray contains 137 µg of azelastine hydrochloride & 50 µg of fluticasone propionate.", 
            dosageInstructions: "-",
        },
        {
            id:4, 
            name:"Isla Junior", 
            image: islajunior,
            description: "Isla junior sore throat lozenges for children. Scientifically reserached to help relieve: Sore, irritated, tickly throats, cough symptoms, horseness, dry throat. Sugar-free and with yummy strawberry flavour. Suitable for 4 years old and above.",
            packSize: "20’s",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Each lozenge contains 80mg Iceland Moss Extract which coats your throat like a balm, with Zinc and Vitamin C to help fight infection.", 
            dosageInstructions: "-",
        },
        {
            id:5, 
            name:"Isla-Cassis / Isla-Mint", 
            image: islacassis,
            description: "Sugar free herbal lozenges. Relieves tickly throat and hoarseness. For singers, teachers and speakers whose vocal cords are constantly under stress. Available in Peppermint and Blackcurrant flavors.",
            packSize: "30’s",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Each lozenge contains 80mg Iceland Moss Extract", 
            dosageInstructions: "-",
        },
        {
            id:6, 
            name:"Nisita", 
            image: nisita,
            description: "Helps relieve blocked nose and colds. Cleans and moisturizes the lining inside the nose, eg in aircon or dry air environment. Suitable for both adults and children.",
            packSize: "Available as nasal spray (20ml) and nasal ointment (20g)",
            productOf: "-",
            exclusiveAvailability: false, 
            ingredients: "Per 100g nasal ointment contains: Sodium chloride 2.8g, Sodium bicarbonate 7.2g / Per ml of nasal spray contains: Sodium chloride 3.3mg, Sodium bicarbonate 8.3mg", 
            dosageInstructions: "Nasal ointment: Apply into nostrils several times daily. / Nasal spray: (Adult & children >2 years) 1 – 2 sprays into each nostril several times daily & (Children 2 years & below) Spray once into each nostril several times daily.",
        },
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setIngredients(message.ingredients);
        setDosage(message.dosageInstructions);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability} ingredients={ingredients} dosageInstructions={dosageInstructions}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default EarNoseThroat;
