import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';

/*Set images URL*/
import belara from '../../../images/products/supplements-hormones-oral-contraceptives/belara-tablet.png';
import norcolut from '../../../images/products/supplements-hormones-oral-contraceptives/norcolut.jpg';
import postinor from '../../../images/products/supplements-hormones-oral-contraceptives/postinor-2.png';

function HormonesOralContraceptives () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);
    const [ingredients, setIngredients] = useState("");
    const [dosageInstructions, setDosage] = useState("");

    const header = "Hormones/Oral contraceptives";
    const category = "Supplements / " + header;
    const products = [
        {
            id:1, 
            name:"Belara Tablet", 
            image: belara,
            description: "Combined Oral Contraceptive Pill. Belara is used to prevent pregnancy in women of childbearing age.",
            packSize: "21’s",
            productOf: "-",
            exclusiveAvailability: true,
            ingredients: "Chlormadinone Acetate 2mg, Ethinylestradiol 0.03mg",
            dosageInstructions: "-",
        },
        {
            id:2, 
            name:"Norcolut", 
            image: norcolut,
            description: "Premenstruation syndrome. Postponement of menstruation. Mastodynia. Dysfunctional uterine bleeding. Endometriosis. Dysfunctional disturbances in bleeding during menopause",
            packSize: "5mg x 20’s tablets",
            productOf: "-",
            exclusiveAvailability: true,
            ingredients: "Norethisterone",
            dosageInstructions: "-",
        }, 
        {
            id:3, 
            name:"Postinor-2", 
            image: postinor,
            description: "Postcoital contraceptive",
            packSize: "0.75mg x 2’s tablets",
            productOf: "-",
            exclusiveAvailability: true,
            ingredients: "Levonorgestrel",
            dosageInstructions: "-",
        }
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setIngredients(message.ingredients);
        setDosage(message.dosageInstructions);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability} ingredients={ingredients} dosageInstructions={dosageInstructions}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default HormonesOralContraceptives;
