import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import { Link } from "react-router-dom";
import { Calendar, Clock, GeoAlt } from 'react-bootstrap-icons';

import upcomingEvents from '../../data/upcomingEvents.json';

function UpcomingEvents() {

    return(
        <>

        <Card>
            <Card.Body>
                {upcomingEvents.map((event, idx) => (
                    <div className="row" key={idx}>
                        <div className="col-4">
                            <Link to="/">
                                <Card className="event-card">
                                    <Card.Img variant="top" src={event.image} />
                                </Card>
                            </Link> 
                        </div>
                        <div className="col-8">
                            <Card className="event-card">
                                <Card.Body>
                                <span className="copyright">{event.date}</span>
                                <Card.Title>{event.name}</Card.Title>
                                <Card.Text>
                                    <p><span className="icon-circle"><Calendar size="16"/></span>Date: {event.date}</p>
                                    <p><span className="icon-circle"><Clock size="16"/></span>Time: {event.time}</p>
                                    <p><span className="icon-circle"><GeoAlt size="16"/></span>Location: {event.location}</p>
                                </Card.Text>
                                </Card.Body>
                            </Card>
                        </div>
                    </div>

                ))}
            </Card.Body>
        </Card>

            {/* <Row xs={1} md={2} className="g-4">
                {upcomingEvents.map((event, idx) => (
                    <Col key={idx}>
                        <Link to="/">
                            <Card className="event-card">
                                <Card.Img variant="top" src={event.image} />
                                <Card.Body>
                                <span className="copyright">{event.date}</span>
                                <Card.Title>{event.name}</Card.Title>
                                <Card.Text>
                                    {event.date}
                                    {event.time}
                                    {event.location}
                                </Card.Text>
                                </Card.Body>
                            </Card>
                        </Link>
                    </Col>
                ))}
            </Row> */}



        </>
    );
}

export default UpcomingEvents;
