import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';

/*Set images URL*/
import legalon from '../../../images/products/supplements-liver-tonic/legalon.png';
import livipids from '../../../images/products/supplements-liver-tonic/livipids-forte.jpg';

function LiverTonic () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);
    const [ingredients, setIngredients] = useState("");
    const [dosageInstructions, setDosage] = useState("");

    const header = "Liver tonic";
    const category = "Supplements / " + header;
    const products = [
        {
            id:1, 
            name:"Legalon", 
            image: legalon,
            description: "Effectively reduces oxidative stress in the liver caused by factors like alcohol abuse, some strong medications, infections, insulin resistance, etc.",
            packSize: "Cap 140 mg x 30’s",
            productOf: "Germany",
            exclusiveAvailability: true,
            ingredients: "Contains Silymarin extract through the patented extraction process, Eurosil 85.",
            dosageInstructions: "Treatment dose: 1 capsule 3 times daily / Maintenance dose: 1 capsule 2 times daily. The capsules should be taken whole with some liquid before meals.",
        },
        {
            id:2, 
            name:"Livipids Forte", 
            image: livipids,
            description: "Supports the vital functions of the liver",
            packSize: "50’s capsules",
            productOf: "-",
            exclusiveAvailability: false,
            ingredients: "Polyunsaturated phospholipids 317mg, Vitamin B1 6mg, Vitamin B2 6mg, Vitamin B6 6mg, Vitamin B12 6mcg, Niacinamide 30mg",
            dosageInstructions: "Livipids Forte: 1 – 2 capsules daily",
        },
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setIngredients(message.ingredients);
        setDosage(message.dosageInstructions);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability} ingredients={ingredients} dosageInstructions={dosageInstructions}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default LiverTonic;
