import { Link } from "react-router-dom";
import { Calendar, Clock, GeoAlt, Calendar2Pl } from 'react-bootstrap-icons';
import Button from 'react-bootstrap/Button';
import Badge from 'react-bootstrap/Badge';
import Alert from 'react-bootstrap/Alert';

import upcomingEvents from '../../data/upcomingEvents.json';

function UpcomingEvents() {

    const event = upcomingEvents[0];
    var eventDetails = event.details;
    var strArrayDetails = "";
    var i;
    for(i = 0; i <eventDetails.length; i++) {
        strArrayDetails += "||" + eventDetails[i];
    }

    return(
        <>


            <Alert variant="light">
            test
            <Alert.Link href="#">an example link</Alert.Link>. Give it a click if
            you like.

            <p><span><Calendar2Plus size="16"/></span><span className="medium">Date: </span></p>
            </Alert>

            <div className="row flex-lg-row-reverse align-items-center g-5 py-5">
                <div className="col-sm-12 col-lg-6 event-card">
                <Link
                    to={{
                        pathname: `/events/${event.urlName}`
                    }}
                    state={{
                        id: `${event.id}`,
                        urlName: `${event.urlName}`,
                        name: `${event.name}`,
                        location: `${event.location}`,
                        date: `${event.date}`,
                        time: `${event.time}`,
                        image: `${event.image}`,
                        details: `${strArrayDetails}`,
                    }}
                    >
                        <img src={upcomingEvents[0].image} className="d-block mx-lg-auto img-fluid" alt={upcomingEvents[0].name}loading="lazy"/>
                    </Link>

                </div>
                <div className="col-sm-12 col-lg-6">
                    <p><Badge bg="warning" text="dark">UPCOMING EVENT</Badge></p>
                    <h3 className="fw-bold lh-1">{upcomingEvents[0].name}</h3>
                    <p>{upcomingEvents[0].description}</p>
                    <p><span className="icon-circle-white"><Calendar size="16"/></span><span className="medium">Date: </span> {upcomingEvents[0].date}</p>
                    <p><span className="icon-circle-white"><Clock size="16"/></span><span className="medium">Time: </span>{upcomingEvents[0].time}</p>
                    <p><span className="icon-circle-white"><GeoAlt size="16"/></span><span className="medium">Location: </span>{upcomingEvents[0].location}</p>

                    <div className="d-grid gap-2 d-md-flex justify-content-md-start">
                        <Button variant="primary raise" href="#contact-us">More details</Button>
                    </div>

                </div>
            </div>
        </>
    );
}

export default UpcomingEvents;
