import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';

/*Set images URL*/
import bacillolspray from '../../../images/products/disinfectant/bacillol-af-spray-wipeon.png';
import bacilloltissues from '../../../images/products/disinfectant/bacillol-af-tissues.png';
import bodedexforte from '../../../images/products/disinfectant/bodedex-forte.jpg';
import korsolexbasic from '../../../images/products/disinfectant/korsolex-basic.jpg';
import mikrobacforte from '../../../images/products/disinfectant/mikrobac-forte.png';
import mikrobactissues from '../../../images/products/disinfectant/mikrobac-tissues.png';
import stelliseptmed from '../../../images/products/disinfectant/stellisept-med.png';
import sterilliumdisinfectant from '../../../images/products/disinfectant/sterillium-hand-disinfectant.png';

function Disinfectants () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);
    const [ingredients, setIngredients] = useState("");
    const [dosageInstructions, setDosage] = useState("");

    const header = " ";
    const category = "Disinfectant / " + header;
    const products = [
        {
            id:1, 
            name:"Bacillol AF Spray/Wipe On", 
            image: bacillolspray,
            description: "Alcohol-based rapid disinfectant with extensive spectrum of activity. Dries without leaving residues. Free of aldehyde, colourant and fragrance. For areas that require quick exposure and drying times and residue free drying. Suitable for use in all dental treatment areas. (spray head not included)",
            packSize: "1L",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Propane-1-ol 450mg/g, Propane-2-ol 250mg/g, Ethanol 47mg/g", 
            dosageInstructions: "-",
        },
        {
            id:2, 
            name:"Bacillol AF Tissues", 
            image: bacilloltissues,
            description: "Alcoholic disinfectant wipes. Rapid disinfection wipes with extensive spectrum of activity in a convenient flow pack. Free of aldehyde, colourant and fragrance.",
            packSize: "80’s, wipe size 180 x 200 mm",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Propane-1-ol 450mg/g, Propane-2-ol 250mg/g, Ethanol 47mg/g", 
            dosageInstructions: "-",
        },
        {
            id:3, 
            name:"Bodedex Forte", 
            image: bodedexforte,
            description: "Surfactant Cleaner for heat-sensitive and heat-resistant instruments for pre-cleaning of endoscopes. Gentle removing of persistent contaminants, esp of contrast media residues and biofilm",
            packSize: "2L, 5L",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Non-ionic & amphoteric surfactants, Solvents, Complexing agents, Amino acid derivatives, Corrosion inhibitors, Anti-foaming agents, Preservative", 
            dosageInstructions: "-",
        },
        {
            id:4, 
            name:"Korsolex Basic", 
            image: korsolexbasic,
            description: "Aldehyde-based disinfectant for heat-sensitive and heat-resistant instruments (incl. flexible endoscopes) with virucidal activity",
            packSize: "5L",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Glutaral, (Ethylenedioxy ) dimethanol, Formaldehyde, But-2-yne-1,4-diol", 
            dosageInstructions: "-",
        },
        {
            id:5, 
            name:"Mikrobac Forte", 
            image: mikrobacforte,
            description: "Aldehyde-free surface disinfectant cleaner with material-friendly protection factor. Suitable for the daily disinfecting maintenance cleaning of all water-resistant surfaces in areas where odourless disinfection is preferred",
            packSize: "250 x 20ml, 5L",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Benzyl-C12-18-alkyldimethyl ammonium chloride, N-(3-aminopropyl)-N-dodecylpropane-1,3-diamine", 
            dosageInstructions: "-",
        },
        {
            id:6, 
            name:"Mikrobac Tissues", 
            image: mikrobactissues,
            description: "Alcohol-free disinfection wipes in the convenient Flowpack. For the cleaning disinfection of alcohol-sensitive surfaces and sensitive medical devices.",
            packSize: "80’s",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Didecyldimethylammonium chloride, Benzyl-C12-18-alkyldimethyl ammonium chloride", 
            dosageInstructions: "-",
        },
        {
            id:7, 
            name:"Stellisept Med", 
            image: stelliseptmed,
            description: "Hygenic Antimicrobial Wash Lotion with triple moisturising complex. Antiseptic Body Wash. Comprehensive activity against bacteria including MRSA and VRE. pH5.5",
            packSize: "100ml, 500ml",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Aqua, PEG-40 Hydrogenated Castor Oil, PEG-150 Distearate, Glycerin, Panthenol, PEG-7 Glyceryl Cocoate, Didecyldimonium Chloride, Allantoin, Sodium Citrate, Parfum", 
            dosageInstructions: "-",
        },
        {
            id:8, 
            name:"Sterillium Hand Disinfectant", 
            image: sterilliumdisinfectant,
            description: "Very good skin tolerance. Dermatologically Tested. Will not dry skin or nails. Bactericidal, yeasticidal, mycobactericidal & virucidal",
            packSize: "	100ml, 500ml, 1L, 5L",
            productOf: "Germany",
            exclusiveAvailability: false, 
            ingredients: "Propan-2-01 450mg/g, Propan-2-01 450mg/g, Mecetronium ethyl sulfate 2mg/g", 
            dosageInstructions: "-",
        },
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setIngredients(message.ingredients);
        setDosage(message.dosageInstructions);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability} ingredients={ingredients} dosageInstructions={dosageInstructions}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default Disinfectants;
