function Products() {

    const params = useParams();
    const productid = params.productid;

    /*To open accordion based on category of item 
    0 is Supplements, 1 is Injury, 2 is Illness, 3 is others as there is no accordion for that category*/
    const [category, setCategory] = useState("0");


	useEffect(()=>{
        if(productid === "dermatological") {
            setCategory("3");
        } else {
            setCategory("0");
        }
	}, [])



