    // } else if (productid === "medical-tools") {
    //     setCategory("3");
    // } else {
    //     setCategory("0");
    // }



    return(
        <>
            <MainNavbar activeTab="/products"/>

            <div className="container section product">
                <div className="row">
                    <div className="col-12 col-sm-4">
                        <ProductNavbar activeTab={productid} category={category}/>
                    </div>


                    <div className="col-12 col-sm-8">
                        <Outlet />
                    </div>
                </div>
            </div>

            
        </>
    );
}

export default Products;
