import { useLocation } from "react-router";
import { ArrowLeft  } from 'react-bootstrap-icons';
import { Link } from "react-router-dom";
import Card from 'react-bootstrap/Card';
import {useState, useEffect} from "react";

import MainNavbar from '../MainNavbar.js';


function EventDetails() {

    // const stateLocation = useLocation();
    // const id = stateLocation.state.id;
    // const urlName = stateLocation.state.urlName;
    // const name = stateLocation.state.name;
    // const location = stateLocation.state.location;
    // const date = stateLocation.state.date;
    // const image = stateLocation.state.image;
    // const eventDetails = stateLocation.state.details;
    const [parseDetails, setParseDetails] = useState([]);

    useEffect(() => {
        // üëáÔ∏è Check if NOT undefined or null

        if(stateLocation !== undefined && stateLocation !== null) {
            const id = stateLocation.state.id;
            const urlName = stateLocation.state.urlName;
            const name = stateLocation.state.name;
            const location = stateLocation.state.location;
            const date = stateLocation.state.date;
            const image = stateLocation.state.image;
            const eventDetails = stateLocation.state.details;
        }

        if (eventDetails !== undefined && eventDetails !== null) {
            setParseDetails(JSON.parse(eventDetails)); 
        }
      }, [eventDetails]);

    return(
        <>
            <MainNavbar activeTab="/events"/>

            <div className="container section eventDetails">
                <div className="row">
                    <div className="col side-title">
                        <div className="mb-4"><Link to="/events"><ArrowLeft size="16"/>Back</Link></div>
                        
                        <div><h3>{name}</h3></div>
                        <hr/>
                    </div>
                </div>


                <div className="row">
                    <div className="col-4 eventImgContainer">
                        <div className="card">
                            <img src={image} className="card-img" alt={name}/>
                        </div>
                    </div>
                    <div className="col-8">
                        <div className="mb-4">
                            <Card>
                                <Card.Body><span className="medium p-4">Date:</span> {date}</Card.Body>
                            </Card>
                        </div>
                        
                        <div className="mb-4">
                            <Card>
                                <Card.Body><span className="medium p-4">Location:</span> {location}</Card.Body>
                            </Card>
                        </div>

                        <div className="mb-4">
                            <Card>
                                <Card.Body><span className="medium p-4">Details:</span> 
                                
                                {}

                                
                                </Card.Body>
                            </Card>


                        </div>
                    </div>
                </div>

            </div>
        </>
    );
}

export default EventDetails;
