import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';

/*Set images URL*/
import eczecareceramide from '../../../images/products/dermatological/eczecare-ceramide-cream.jpg';
import eczecareintensive from '../../../images/products/dermatological/eczecare-ceramide-intensive-cream.jpg';
import eczecarerapid from '../../../images/products/dermatological/eczecare-ceramide-rapid-itch-relief-cream.png';
import eczecaredryskin from '../../../images/products/dermatological/eczecare-dry-skin-treatment.jpg';
import eczecareoatmeal from '../../../images/products/dermatological/eczecare-oatmeal-range.jpg';
import elidel from '../../../images/products/dermatological/elidel-cream.jpg';

function Dermatological () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);
    const [ingredients, setIngredients] = useState("");
    const [dosageInstructions, setDosage] = useState("");

    const header = "Dermatological";
    const category = " " + header;
    const products = [
        {
            id:1, 
            name:"EcZeCare Ceramide Cream", 
            image: eczecareceramide,
            description: "An innovative blend of natural moisturising factors in a unique formulation to hydrate your skin. For eczema-prone skin. Repairs skin barrier. Intense hydration. Strong moisturising factors. Impedes transepidermal water loss and smoothen skin. PH5.5,  lanolin-free, paraben-free , fragrance-free. Suitable for both babies and adults",
            packSize: "50ml, 250ml, 500ml",
            productOf: "-",
            exclusiveAvailability: false, 
            ingredients: "Ceramide-3 dominant, Fatty Acid, Cholesterol, Sodium PCA, Amino Acids, Dimethicone", 
            dosageInstructions: "-",
        },
        {
            id:2, 
            name:"EcZeCare Ceramide Intensive Cream", 
            image: eczecareintensive,
            description: "An innovative blend of natural moisturising factors in a unique formulation to hydrate your skin. For eczema-prone skin, Repairs skin barrier, Reduces inflammation, Intense hydration, Strong moisturising factors, Impedes transepidermal water loss and smoothen skin. PH5.5,  lanolin-free, paraben-free , fragrance-free. Suitable for both babies and adults",
            packSize: "50ml",
            productOf: "-",
            exclusiveAvailability: false, 
            ingredients: "Ceramide-3 dominant, Colloidal Oatmeal, Fatty Acid, Cholesterol, Sodium PCA, Amino Acids, Dimethicone", 
            dosageInstructions: "-",
        },
        {
            id:3, 
            name:"EcZeCare Ceramide Rapid Itch Relief Cream", 
            image: eczecarerapid,
            description: "An innovative blend of natural moisturising factors in a unique formulation to hydrate your skin with rapid itch relief. Rapid itch relief, For eczema-prone skin, Repairs skin barrier, Intense hydration, Strong moisturising factors, Impedes transepidermal water loss and smoothen skin. PH5.5,  lanolin-free, paraben-free , fragrance-free. Suitable for adults and children above 3 years old",
            productOf: "-",
            exclusiveAvailability: true, 
            ingredients: "Ceramide-3 dominant, Menthol, Fatty Acid, Cholesterol, Sodium PCA, Amino Acids, Dimethicone", 
            dosageInstructions: "-",
        },
        {
            id:4, 
            name:"EcZeCare Dry Skin Treatment", 
            image: eczecaredryskin,
            description: "Suitable for dry skin and chronic dry skin conditions",
            packSize: "50ml, 250ml, 500ml",
            productOf: "-",
            exclusiveAvailability: false, 
            ingredients: "Formulated with triple moisturisers: urea, sodium PCA and lactic acid", 
            dosageInstructions: "-",
        },
        {
            id:5, 
            name:"EcZeCare Oatmeal range", 
            image: eczecareoatmeal,
            description: "Soap free wash, moisturizing lotion, shower & bath oil, shampoo, cream. For dry, itchy or sensitive skin. Restores normal pH balance of the skin. Suitable for both adults and children",
            packSize: "Available in 250ml and 500ml (EcZeCare cream is available only in 50g tube)",
            productOf: "-",
            exclusiveAvailability: false, 
            ingredients: "Contains natural colloidal oatmeal", 
            dosageInstructions: "-",
        },
        {
            id:6, 
            name:"Elidel Cream", 
            image: elidel,
            description: "Elidel 1% cream is indicated for the short-term and intermittent long-term therapy in the treatment of mild to moderate atopic dermatitis.",
            packSize: "15g tube",
            productOf: "France",
            exclusiveAvailability: true, 
            ingredients: "Pimecrolimus 1%", 
            dosageInstructions: "-",
        },
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setIngredients(message.ingredients);
        setDosage(message.dosageInstructions);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability} ingredients={ingredients} dosageInstructions={dosageInstructions}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default Dermatological;
