import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';
import medicalToolsJson from "../../../data/products/medicalTools/medicalTools.json";

function MedicalTools () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);
    const [ingredients, setIngredients] = useState("");
    const [dosageInstructions, setDosage] = useState("");

    const header = "Medical tools";
    const category = " " + header;
    const products = [
        {
            id:1, 
            name:"Securline", 
            image: securline,
            description: "Surgical skin marker for hospital use. It comes with a versatile tip with precise fine line marking or broad line marking (when held at an angle). Gentian violet ink used is nontoxic, non-tatooing and surgical prep-resistant. Markings can be removed from the skin with isopropyl alcohol. Includes a separate six inch, flexible ruler calibrated in both inches and centimeters.",
            packSize: "10’s pieces per box",
            productOf: "USA",
            exclusiveAvailability: false, 
            ingredients: "-", 
            dosageInstructions: "-",
        },
        {
            id:2, 
            name:"Stille", 
            image: stille,
            description: "Reusable surgical instruments from Sweden. Each instrument is made of top quality stainless steel, handcrafted by skilled instrument makers, and undergoes rigorous quality inspection.",
            packSize: "-",
            productOf: "Sweden",
            exclusiveAvailability: false, 
            ingredients: "-", 
            dosageInstructions: "-",
        },
        {
            id:3, 
            name:"Toray", 
            image: toray,
            description: "Inoue-Balloon catheter set for PTMC (Percutaneous Transvenous Mitral Commissurotomy) in patients with mitral stenosis",
            packSize: "-",
            productOf: "Japan",
            exclusiveAvailability: false, 
            ingredients: "-", 
            dosageInstructions: "-",
        },
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setIngredients(message.ingredients);
        setDosage(message.dosageInstructions);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability} ingredients={ingredients} dosageInstructions={dosageInstructions}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default MedicalTools;
