import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';

/*Set images URL*/
import goon from '../../../images/products/supplements-joint-health/go-on.jpg';
import viartril from '../../../images/products/supplements-joint-health/viartril-s.png';

function JointHealth () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);
    const [ingredients, setIngredients] = useState("");
    const [dosageInstructions, setDosage] = useState("");

    const header = "Joint health";
    const category = "Supplements / " + header;
    const products = [
        {
            id:1, 
            name:"GO-ON", 
            image: goon,
            description: "For severe joint pain. Provides lubrication and mechanical support in the painful, creaky joints.",
            packSize: "Injectable (pre-filled syringe) 25 mg/2.5 mL x 1’s",
            productOf: "Ireland",
            exclusiveAvailability: false,
            ingredients: "1% Sodium Hyaluronate (intra-articular injection)",
            dosageInstructions: "Adults: Go-On should be injected into the affected joint a total of 3 to 5 times at weekly intervals. Several joints may be treated concurrently. Treatment cycles may be repeated if required.",
        },
        {
            id:2, 
            name:"Viartril-S", 
            image: viartril,
            description: "The only glucosamine that has been clinically proven to reduce knee surgery by 57%! Contains the clinically tested Crystalline Glucosamine Sulphate for mild to moderate joint pain. Originally formulated and patented by Rottapharm and has been approved as a prescription drug in Europe for the treatment of osteoarthritis. The only glucosamine proven safe and efficacious in hundreds of clinical papers.",
            packSize: "-",
            productOf: "Ireland",
            exclusiveAvailability: false,
            ingredients: "-",
            dosageInstructions: "Capsule: 2 capsules 250 mg or 1 capsule 500 mg, 3 times daily / Powder for Oral Solution: 1 sachet (dissolved in a glass of water) once daily. The product should be preferably taken at meals.",
        },
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setIngredients(message.ingredients);
        setDosage(message.dosageInstructions);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability} ingredients={ingredients} dosageInstructions={dosageInstructions}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default JointHealth;
