import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import { Link } from "react-router-dom";

/*
+ Upload images to the folder
+ Indicate image folder path
*/
import img1 from '../../images/events/cme-talk-for-doctors-the-sporting-knee/subimg1.jpg';
import img2 from '../../images/events/orthopaedic-public-forum-at-khoo-teck-puat-hospital/img1.jpg';

function UpcomingEvents() {

    /*Please indicate the total number of upcoming events*/
    const totalNumberOfUpcomingEvents = 2;

    /*Create as many events as needed. ID of events has to be unique.*/
    const events = [
        {
            id:1, 
            name:"CME Talk for Doctors: The Sporting Knee",
            date: "01 Jan 2023",
            description: "This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.",
            image: img1,
        },
        {
            id:2, 
            name:"Orthopaedic Public Forum at Khoo Teck Puat Hospital (in Mandarin)", 
            date: "22 Jan 2023",
            description: "This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.",
            image: img2,
        },
    ];


    return(
        <>
            <Row xs={1} md={{totalNumberOfUpcomingEvents}} className="g-4">
                {events.map((event, idx) => (
                    <Col key={idx}>
                        <Link to="/">
                            <Card className="event-card">
                                <Card.Img variant="top" src={event.image} />
                                <Card.Body>
                                <span className="copyright">{event.date}</span>
                                <Card.Title>{event.name}</Card.Title>
                                <Card.Text>
                                    {event.description}
                                </Card.Text>
                                </Card.Body>
                            </Card>
                        </Link>
                    </Col>
                ))}
            </Row>
        </>
    );
}

export default UpcomingEvents;
