import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import { Link } from "react-router-dom";

import img1 from '../../images/events/cme-talk-for-doctors-the-sporting-knee/subimg1.jpg';
import img2 from '../../images/events/orthopaedic-public-forum-at-khoo-teck-puat-hospital/img1.jpg';

function UpcomingEvents() {


    const events = [
        {
            id:1, 
            name:"CME Talk for Doctors: The Sporting Knee", 
            image: img1,
        },
        {
            id:2, 
            name:"Orthopaedic Public Forum at Khoo Teck Puat Hospital (in Mandarin)", 
            image: img2,
        },
    ];

    const eventList = events.map((event) =>(

        <div key={event.id}>
            <Col>
                <Link to="/">
                    <div className="card mb-3 hoverable">
                        <div className="row no-gutters">
                            <div className="col-md-4">
                            <img src={event.image} className="card-img" alt={event.name}/>
                            </div>
                            <div className="col-md-8">
                                <div className="card-body">
                                    <h5 className="card-title">{event.name}</h5>
                                </div>
                            </div>
                        </div>
                    </div>
                </Link>
            </Col>
        </div>

    ));

    return(
        <>
            <div className="row upcoming-events">
                <div className="col">
                <h5>Upcoming events</h5>
                    <Row xs={1} md={2} className="g-4">
                        {events.map((event, idx) => (
                            <Col key={idx}>
                            <Card className="event-card">
                                <Card.Img variant="top" src={img1} />
                                <Card.Body>
                                <Card.Title>{event.name}</Card.Title>
                                <Card.Text>
                                    This is a longer card with supporting text below as a natural
                                    lead-in to additional content. This content is a little bit
                                    longer.
                                </Card.Text>
                                </Card.Body>
                            </Card>
                            </Col>
                        ))}
                    </Row>
                </div>
            </div>
        </>
    );
}

export default UpcomingEvents;
