import { Link } from "react-router-dom";
import { useState } from 'react';

import ItemModal from '../ItemModal.js';

/*Set images URL*/
import cavinton from '../../../images/products/supplements-brain-boosters/cavinton.jpg';

function BrainBoosters () {

    const [show, setShow] = useState(false);
    const [name, setName] = useState("");
    const [image, setImage] = useState("");
    const [description, setDescription] = useState("");
    const [packSize, setPackSize] = useState("");
    const [productOf, setProductOf] = useState("");
    const [exclusiveAvailability, setAvailability] = useState(false);

    const category = "Supplements / Brain boosters/memory enhancers";
    const header = "Brain Boosters/memory enhancers";
    const products = [
        {
            id:1, 
            name:"Cavinton", 
            image: cavinton,
            description: "Cerebral circulatory disorders of various origin. Hypertensive encephalopathy. Intermittent vascular cerebral insufficiency. Improvement of collateral circulation in ischaemic cerebral injuries and in advanced cerebral arteriosclerosis.",
            packSize: "5mg x 50’s tablets",
            productOf: "Hungary",
            exclusiveAvailability: true, /*False or true. Only available in clinics and specialised centers*/
            ingredients: "Vinpocetine", /*Separate ingredients by comma (i.e ABC, EFG, HIJ)*/
            dosageInstructions: "-",
        },
    ];

    const productList = products.map((product) =>(

        <div className="col col-sm-4 itemCard" key={product.id}>
            <div className="itemImage">
                <Link href={() => false} onClick={() => displayItemModal(product)}><img src={product.image} alt={product.name}/> </Link>
            </div>
            <div className="itemInfo">
                <Link href={() => false} onClick={() => displayItemModal(product)}><p className="itemName">{product.name}</p> </Link>
                <p className="itemDesc">{category}</p>
            </div>
        </div>

    ));

    /*This is to close the modal and reset the value*/
    function callBack (childData) {
        setShow(false);
    }

    const displayItemModal = (message) => {
        setName(message.name);
        setImage(message.image);
        setDescription(message.description);
        setPackSize(message.packSize);
        setProductOf(message.productOf);
        setAvailability(message.exclusiveAvailability);
        setShow(true);
    }

    return (
        <>

            <div className="side-title">
                <h5>{header}</h5>
                <hr></hr>
            </div>

            <div className="row itemContainer">
                {productList}
            </div>


            <div>
                {show ? (
                    <ItemModal handleCallBack={callBack} name={name} image={image} description={description} packSize={packSize} productOf={productOf} exclusiveAvailability={exclusiveAvailability}/>
                ) : (
                    ""
                )}
            </div>

        </>
    );
}

export default BrainBoosters;
